import React from 'react';
import { Slider } from '@mantine/core';
import { useHover } from '@mantine/hooks';

export function SliderHover() {
  const { hovered, ref } = useHover();

  return (
    <Slider
      defaultValue={40}
      min={10}
      max={90}
      ref={ref}
      label={null}
      styles={{
        thumb: {
          transition: 'opacity 150ms ease',
          opacity: hovered ? 1 : 0,
        },

        dragging: {
          opacity: 1,
        },
      }}
    />
  );
}

import React from 'react';
import { Slider, RangeSlider } from '@mantine/core';
import { HeartFillIcon, HeartIcon } from '@primer/octicons-react';

const styles = { thumb: { borderWidth: 2, height: 26, width: 26, padding: 3 } };

export function SliderIcon() {
  return (
    <>
      <Slider
        thumbChildren={<HeartFillIcon size={12} />}
        color="red"
        label={null}
        defaultValue={40}
        styles={styles}
      />

      <RangeSlider
        mt="xl"
        styles={styles}
        color="red"
        label={null}
        defaultValue={[20, 60]}
        thumbChildren={[<HeartIcon size={12} key="1" />, <HeartFillIcon size={12} key="2" />]}
      />
    </>
  );
}

import React, { useState } from 'react';
import { createStyles, NumberInput, Slider } from '@mantine/core';

const useStyles = createStyles((theme) => ({
  wrapper: {
    position: 'relative',
  },

  input: {
    height: 'auto',
    paddingTop: 22,
    paddingBottom: 3,
    borderBottomRightRadius: 0,
    borderBottomLeftRadius: 0,
  },

  label: {
    position: 'absolute',
    pointerEvents: 'none',
    paddingLeft: theme.spacing.sm,
    paddingTop: theme.spacing.sm / 2,
    zIndex: 1,
  },

  slider: {
    position: 'absolute',
    width: '100%',
    bottom: -1,
  },

  thumb: {
    width: 16,
    height: 16,
  },

  track: {
    backgroundColor: theme.colorScheme === 'dark' ? theme.colors.dark[3] : theme.colors.gray[4],
  },
}));

export function SliderInput() {
  const { classes } = useStyles();
  const [value, setValue] = useState(2200);
  return (
    <div className={classes.wrapper}>
      <NumberInput
        value={value}
        onChange={setValue}
        label="Your daily kcal consumption"
        placeholder="2200 is an average value"
        step={50}
        min={0}
        max={8000}
        hideControls
        classNames={{ input: classes.input, label: classes.label }}
      />
      <Slider
        max={8000}
        step={50}
        min={0}
        label={null}
        value={value}
        onChange={setValue}
        size={2}
        radius={0}
        className={classes.slider}
        classNames={{ thumb: classes.thumb, track: classes.track }}
      />
    </div>
  );
}

import React from 'react';
import { createStyles, RangeSlider } from '@mantine/core';

const useStyles = createStyles((theme) => ({
  label: {
    top: 0,
    height: 28,
    width: 34,
    padding: 0,
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    fontWeight: 700,
    backgroundColor: 'transparent',
  },

  thumb: {
    backgroundColor: theme.colors[theme.primaryColor][6],
    height: 28,
    width: 34,
    border: 'none',
  },

  dragging: {
    transform: 'translate(-50%, -50%)',
  },
}));

export function SliderLabel() {
  const { classes } = useStyles();
  return <RangeSlider labelAlwaysOn defaultValue={[20, 60]} classNames={classes} />;
}

import React from 'react';
import { createStyles, RangeSlider } from '@mantine/core';
import { Point, GripVertical } from 'tabler-icons-react';

const useStyles = createStyles((theme) => ({
  mark: {
    display: 'none',
  },

  markWrapper: {
    marginTop: 12,
  },

  thumb: {
    width: 16,
    height: 28,
    backgroundColor: theme.white,
    color: theme.colors.gray[5],
    border: `1px solid ${
      theme.colorScheme === 'dark' ? theme.colors.dark[2] : theme.colors.gray[3]
    }`,
  },
}));

const point = <Point size={10} style={{ marginTop: 6 }} />;

export function SliderMarks() {
  const { classes } = useStyles();
  return (
    <RangeSlider
      mt="xl"
      mb="xl"
      classNames={classes}
      defaultValue={[30, 60]}
      thumbChildren={<GripVertical />}
      marks={[
        { value: 0, label: '0' },
        { value: 12.5, label: point },
        { value: 25, label: '25' },
        { value: 37.5, label: point },
        { value: 50, label: '50' },
        { value: 62.5, label: point },
        { value: 75, label: '75' },
        { value: 87.5, label: point },
        { value: 100, label: '100' },
      ]}
    />
  );
}

import React from 'react';
